CREATE TABLE NAZNACH (ID BIGINT GENERATED BY DEFAULT AS IDENTITY (START WITH 1 INCREMENT BY 1) NOT NULL, CEX INTEGER, CLASSN INTEGER, DATNAZ DATE, KAT INTEGER, PROF INTEGER, SOVM BOOLEAN, UCH INTEGER, ID_PRIEM BIGINT, PRIMARY KEY (ID))
CREATE TABLE OPLATA (ID BIGINT GENERATED BY DEFAULT AS IDENTITY (START WITH 1 INCREMENT BY 1) NOT NULL, DATKNO DATE, DATSO DATE, KNO VARCHAR(255), NADB REAL, SO INTEGER, TSO REAL, ZANCNT VARCHAR(255), ZCNT VARCHAR(255), ID_NAZNACH BIGINT, PRIMARY KEY (ID))
CREATE TABLE PDATA (PID BIGINT GENERATED BY DEFAULT AS IDENTITY (START WITH 1 INCREMENT BY 1) NOT NULL, A_NDOM VARCHAR(255), A_NKV VARCHAR(255), A_OBL VARCHAR(255), A_RAJON VARCHAR(255), A_TNP VARCHAR(255), A_TOWN VARCHAR(255), A_TUL VARCHAR(255), A_UL VARCHAR(255), BANK VARCHAR(255), BANK_ACC VARCHAR(255), MESTOROGD VARCHAR(255), OBRAZOV VARCHAR(255), PASP_DATEV DATE, PASP_KEMV VARCHAR(255), PASP_NOM VARCHAR(255), PASP_SER VARCHAR(255), PHONE VARCHAR(255), PIDCOD VARCHAR(255), POL VARCHAR(255), POST_IND VARCHAR(255), PRIMARY KEY (PID))
CREATE TABLE PERSON (PID BIGINT GENERATED BY DEFAULT AS IDENTITY (START WITH 1 INCREMENT BY 1) NOT NULL, DATAR DATE, FAM VARCHAR(255), IM VARCHAR(255), OT VARCHAR(255), PRIMARY KEY (PID))
CREATE TABLE PRIEM (ID BIGINT NOT NULL, DATNSTAG DATE, DATOSTAG DATE, DATP DATE, DATUV DATE, PRFS VARCHAR(255), PRUV INTEGER, TN VARCHAR(255), USLPR INTEGER, PID BIGINT, PRIMARY KEY (ID))
ALTER TABLE NAZNACH ADD CONSTRAINT FK_NAZNACH_ID_PRIEM FOREIGN KEY (ID_PRIEM) REFERENCES PRIEM (ID)
ALTER TABLE OPLATA ADD CONSTRAINT FK_OPLATA_ID_NAZNACH FOREIGN KEY (ID_NAZNACH) REFERENCES NAZNACH (ID)
ALTER TABLE PDATA ADD CONSTRAINT FK_PDATA_PID FOREIGN KEY (PID) REFERENCES PERSON (PID)
ALTER TABLE PRIEM ADD CONSTRAINT FK_PRIEM_PID FOREIGN KEY (PID) REFERENCES PERSON (PID)
